mapper.select:
SELECT *
	FROM (
		SELECT ROWNUM AS RN, A.*
			FROM (
				
				
					
						MEMBER_NAME LIKE '%' || #{memberName} || '%'
					
					
						AND MEMBER_GENDER = #{memberGender} 
					
				
				ORDER BY MEMBER_ID DESC
				) A
	)
	
	
				
		 ROWNUM  <=  #{pageSize, jdbcType=INTEGER}
			AND RN  >  (#{pageNo, jdbcType=INTEGER}-1)*#{pageSize, jdbcType=INTEGER}

mapper.insert:
INSERT INTO STUDENT (
	  MEMBER_ID    
	, MEMBER_PW    
	, MEMBER_NAME  
	, MEMBER_BIRTH 
	, MEMBER_GENDER
	, MEMBER_EMAIL 
	, FILES_ID
)
VALUES (
	  #{memberId} 	
	, #{memberPw}
	, #{memberName}
	, #{memberBirth}
	, #{memberGender}
	, #{memberEmail}
	, #{filesId}
)

mapper.update:
UPDATE STUDENT

	  MEMBER_PW    = #{memberPw}    
	, MEMBER_NAME  = #{memberName}  
	, MEMBER_BIRTH = #{memberBirth} 
	, MEMBER_GENDER= #{memberGender}
	, MEMBER_EMAIL = #{memberEmail} 

WHERE 
	MEMBER_ID = #{memberId}
